{"changed":true,"filter":false,"title":"login.php","tooltip":"/regSystem/login.php","value":"<?php\n\nif (isset($_POST['login-submit'])) {\n    \n    require 'dBHandler.php';\n    \n    $emailuid = $_POST['username'];\n    $password = $_POST['user-pwd'];\n    \n    if (empty($emailuid) || empty($password)) {\n        header(\"Location: ../signInPage.php?error=emptyfields\");\n        exit();\n    }\n    \n    else {\n        $sql = \"SELECT * FROM users WHERE user_uid=?;\";\n        $stmt = mysqli_stmt_init($conn);\n        if (!mysqli_stmt_prepare($stmt, $sql)) {\n            header(\"Location: ../signInPage.php?error=sqlerror\");\n            exit();\n        }\n        \n        //Prepared statement\n        else {\n            mysqli_stmt_bind_param($stmt, \"s\", $emailuid);\n            mysqli_stmt_execute($stmt);\n            $result = mysqli_stmt_get_result($stmt);\n            \n            //Check if we have a result - set it equal to a variable\n            //Take password from matched user\n            //Hash the password that the user logged in with and compare\n            if ($row = mysqli_fetch_assoc($result)) {\n                $pwdCheck = password_verify($password, $row['user_pwd']);\n                \n                if ($pwdCheck == false) {\n                    header(\"Location: ../signInPage.php?error=wrongpwd\");\n                    exit();\n                }\n                \n                //User types correct username & password\n                //Start session\n                else if ($pwdCheck == true) {\n                    session_start();\n                    $_SESSION['userId'] = $row['user_id'];\n                    $_SESSION['userUid'] = $row['user_uid'];\n                    \n                    header(\"Location: ../landing.php?login=success\");\n                    exit();\n                }\n                \n                else {\n                    header(\"Location: ../signInPage.php?error=wrongpwd\");\n                    exit();\n                }\n            }\n            \n            else {\n                header(\"Location: ../signInPage.php?error=nouser\");\n                exit();\n            }\n        }\n    }\n    \n}\n\nelse {\n    header(\"Location: ../landing.php\");\n    exit();\n}","undoManager":{"mark":98,"position":98,"stack":[[{"start":{"row":10,"column":29},"end":{"row":10,"column":38},"action":"remove","lines":["index.php"],"id":1},{"start":{"row":10,"column":29},"end":{"row":10,"column":43},"action":"insert","lines":["signInPage.php"]},{"start":{"row":18,"column":33},"end":{"row":18,"column":42},"action":"remove","lines":["index.php"]},{"start":{"row":18,"column":33},"end":{"row":18,"column":47},"action":"insert","lines":["signInPage.php"]},{"start":{"row":35,"column":41},"end":{"row":35,"column":50},"action":"remove","lines":["index.php"]},{"start":{"row":35,"column":41},"end":{"row":35,"column":55},"action":"insert","lines":["signInPage.php"]},{"start":{"row":46,"column":41},"end":{"row":46,"column":50},"action":"remove","lines":["index.php"]},{"start":{"row":46,"column":41},"end":{"row":46,"column":55},"action":"insert","lines":["signInPage.php"]},{"start":{"row":51,"column":41},"end":{"row":51,"column":50},"action":"remove","lines":["index.php"]},{"start":{"row":51,"column":41},"end":{"row":51,"column":55},"action":"insert","lines":["signInPage.php"]},{"start":{"row":57,"column":37},"end":{"row":57,"column":46},"action":"remove","lines":["index.php"]},{"start":{"row":57,"column":37},"end":{"row":57,"column":51},"action":"insert","lines":["signInPage.php"]}],[{"start":{"row":4,"column":21},"end":{"row":4,"column":22},"action":"remove","lines":["r"],"id":2}],[{"start":{"row":4,"column":20},"end":{"row":4,"column":21},"action":"remove","lines":["e"],"id":3}],[{"start":{"row":4,"column":19},"end":{"row":4,"column":20},"action":"remove","lines":["l"],"id":4}],[{"start":{"row":4,"column":18},"end":{"row":4,"column":19},"action":"remove","lines":["d"],"id":